// Extract and validate prd_content from webhook body
const body = $json.body || $json;
const prdContent = body.prd_content || body.content || body.prd || '';

if (!prdContent) {
  return {
    status: 400,
    error: 'Missing required field: prd_content',
    message: 'Request body must include prd_content field with PRD markdown text'
  };
}

return {
  prd_content: prdContent,
  valid: true
};

// Extract project name from title
const content = $json.prd_content;
let projectName = null;

const titleMatch = content.match(/^#\s+PRD:\s+(.+)$/m);
if (titleMatch) {
  projectName = titleMatch[1].trim();
}

return {
  ...($json),
  project_name: projectName
};

// Extract client name
const content = $json.prd_content;
let clientName = null;

const clientMatch = content.match(/\*\*Client:\*\*\s+(.+?)(?:\n|$)/m);
if (clientMatch) {
  clientName = clientMatch[1].trim();
}

return {
  ...($json),
  client_name: clientName
};

// Count user stories and tasks
const content = $json.prd_content;

// User stories
const userStoryMatches = content.match(/###\s+US-\d+:/g) || [];
const totalUserStories = userStoryMatches.length;

// Tasks
const completedTasks = (content.match(/^- \[x\]/gmi) || []).length;
const incompleteTasks = (content.match(/^- \[ \]/gmi) || []).length;
const skippedTasks = (content.match(/\[SKIPPED\]/gi) || []).length;

// Phases
const phaseMatches = content.match(/PHASE\s+(\d+):/g) || [];
const checkpointMatches = content.match(/\[CHECKPOINT:\s+(.+?)\]/g) || [];

// Goals
const goalsSection = content.match(/## Goals([\s\S]*?)(?=\n## |\n=|$)/);
const goals = goalsSection ? (goalsSection[1].match(/^- (.+)$/gm) || []).map(g => g.substring(2).trim()) : [];

// Tech stack
const techSection = content.match(/## Technical Stack([\s\S]*?)(?=\n## |\n=|$)/);
const techStack = techSection ? (techSection[1].match(/^- \*\*(.+?):\*\* (.+)$/gm) || []).map(t => t.substring(2).trim()) : [];

return {
  status: 200,
  message: 'PRD analysis complete',
  data: {
    project_name: $json.project_name,
    client_name: $json.client_name,
    total_user_stories: totalUserStories,
    tasks: {
      total: completedTasks + incompleteTasks,
      completed: completedTasks,
      incomplete: incompleteTasks,
      skipped: skippedTasks
    },
    phases: {
      total: phaseMatches.length,
      checkpoints: checkpointMatches.length
    },
    goals: goals,
    tech_stack: techStack
  }
};
